import React, { useState } from "react"
import Card from "../UI/Card"
import Button from "../UI/Button"
import styles from './CreateUser.module.css'
import ErrorModal from "../UI/ErrorModal"

const CreateUser = (props) => {

    const [inputName, setInputName] = useState('')
    const [inputAge, setInputAge] = useState('')
    const [error, setError] = useState()

    const createUserHandler = (event) => {
        event.preventDefault()
        if (inputName.trim().length === 0 || inputAge.trim().length === 0) {
            setError({
                title: 'Incorrect input',
                message: 'These fields cannot be empty'
            })
            return
        }

        if (+inputAge < 1) {
            setError({
                title: 'Incorrect age',
                message: 'The age must be greater than 0'
            })
            return
        }

        /* console.log(inputName, inputAge); */
        props.onCreateUser(inputName, inputAge)
        setInputName('')
        setInputAge('')
    }

    const nameChangeHandler = (event) => {
        setInputName(event.target.value)
    }

    const ageChangeHandler = (event) => {
        setInputAge(event.target.value)
    }

    return (
        <div>
        {error && <ErrorModal title={error.title} message={error.message}/>}
        <Card className={styles.input}>
            <form onSubmit={createUserHandler}>
                <label htmlFor="name">Name</label>
                <input id="name" type="text" onChange={nameChangeHandler} value={inputName}/>
                <label htmlFor="age">Age</label>
                <input id="age" type="number" onChange={ageChangeHandler} value={inputAge}/>
                <Button type="submit">Add User</Button>
            </form>
        </Card>
        </div>
    )
}

export default CreateUser
